package eu.europa.esig.dss.tsl.dto.condition;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;

import java.util.Arrays;

import org.junit.jupiter.api.Test;

import eu.europa.esig.dss.model.x509.CertificateToken;
import eu.europa.esig.dss.spi.DSSUtils;

public class QCStatementConditionTest {

	@Test
	public void test() {
		CertificateToken certificate = DSSUtils.loadCertificateFromBase64EncodedString(
				"");

		QCStatementCondition c = new QCStatementCondition(Arrays.asList("0.4.0.1862.1.1"));
		assertTrue(c.check(certificate));

		QCStatementCondition c1 = new QCStatementCondition(Arrays.asList("0.4.0.1862.1.2"));
		assertFalse(c1.check(certificate));

		QCStatementCondition c2 = new QCStatementCondition(Arrays.asList("0.4.0.1862.1.1", "0.4.0.1862.1.1"));
		assertTrue(c2.check(certificate));

		QCStatementCondition c3 = new QCStatementCondition(Arrays.asList("0.4.0.1862.1.2", "0.4.0.1862.1.1"));
		assertFalse(c3.check(certificate));
	}

}
